@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MainScreen(
 viewModel: MainViewModel,
 onNavigateToSettings: () -> Unit = {},
 onNavigateToCategories: () -> Unit = {},
 onNavigateToDashboard: () -> Unit = {},
) {
 var openBottomSheet by rememberSaveable { mutableStateOf(false) }

 // Handle shared URL from MainActivity
 LaunchedEffect(Unit) {
 MainActivity.sharedUrl?.let { url ->
 viewModel.setSharedUrl(url)
 openBottomSheet = true
 MainActivity.sharedUrl = null // Clear the shared URL after handling
 }
 }

 // Add the bottom sheet
 ModalBottomSheetAddUrl(
 openBottomSheet = openBottomSheet,
 onOpenBottomSheetChange = { openBottomSheet = it },
 )

 EnterAlwaysTopAppBar(
 viewModel = viewModel,
 onNavigateToSettings = onNavigateToSettings,
 onNavigateToCategories = onNavigateToCategories,
 onNavigateToDashboard = onNavigateToDashboard,
 openBottomSheet = { openBottomSheet = true },
 )
}

@OptIn(ExperimentalMaterial3Api::class, ExperimentalMaterial3ExpressiveApi::class)
@Composable
fun EnterAlwaysTopAppBar(
 viewModel: MainViewModel,
 onNavigateToSettings: () -> Unit = {},
 onNavigateToCategories: () -> Unit = {},
 onNavigateToDashboard: () -> Unit = {},
 openBottomSheet: () -> Unit,
) {
 val scrollBehavior = TopAppBarDefaults.enterAlwaysScrollBehavior()
 val coroutineScope = rememberCoroutineScope()
 val listState = rememberLazyListState()
 val links by viewModel.links.collectAsState()

 val isTopAppBarOffScreen by remember {
 derivedStateOf {
 scrollBehavior.state.heightOffset < -5
 }
 }
 val snackbarHostState = remember { SnackbarHostState() }

 Scaffold(
 snackbarHost = { SnackbarHost(snackbarHostState) },
 modifier = Modifier.nestedScroll(scrollBehavior.nestedScrollConnection),
 topBar = {
 TopAppBar(
 title = {
 Text(
 text = stringResource(id = R.string.main_screen_title),
 color = MaterialTheme.colorScheme.primary,
 maxLines = 1,
 overflow = TextOverflow.Ellipsis,
 )
 },
 subtitle = { Text("Your saved links.", maxLines = 1, overflow = TextOverflow.Ellipsis) },
 scrollBehavior = scrollBehavior,
 actions = {
 TopAppBarIcon { shareApp() }

 },
 )
 },
 bottomBar = {
 BottomAppBar(
 containerColor = MaterialTheme.colorScheme.surface,
 actions = {
 IconButton(onClick = { onNavigateToCategories() }) {
 Icon(
 imageVector = Icons.Rounded.Category,
 contentDescription = "Categories",
 tint = MaterialTheme.colorScheme.onSurface,
 )
 }

 IconButton(onClick = { onNavigateToDashboard() }) {
 Icon(
 imageVector = Icons.Rounded.Dashboard,
 contentDescription = "Dashboard",
 tint = MaterialTheme.colorScheme.onSurface,
 )
 }

 IconButton(onClick = { onNavigateToSettings() }) {
 Icon(
 imageVector = Icons.Rounded.Settings,
 contentDescription = "Settings",
 tint = MaterialTheme.colorScheme.onSurface,
 )
 }
 },
 floatingActionButton = {
 ExtendedFloatingActionButton(
 onClick = openBottomSheet,
 icon = { Icon(Icons.Rounded.Add, "Add a Link", tint = MaterialTheme.colorScheme.onPrimary) },
 text = { Text(text = "Add a Link") },
 containerColor = MaterialTheme.colorScheme.primary,
 shape = RoundedCornerShape(16.dp),
 )
 },
 )
 },
 content = { innerPadding ->
 Box(modifier = Modifier.fillMaxSize()) {
 Column(
 modifier = Modifier
 .padding(innerPadding)
 .fillMaxSize(),
 ) {
 CollapsingHeader(viewModel = viewModel, isTopAppBarOffScreen = isTopAppBarOffScreen)
 LinksContent(
 viewModel = viewModel,
 listState = listState,
 openBottomSheet = openBottomSheet,
 onDelete = { link ->
 viewModel.deleteLink(link)

 coroutineScope.launch {
 val result = snackbarHostState
 .showSnackbar(
 message = "Category ${link.name} deleted",
 actionLabel = "Undo",
 duration = SnackbarDuration.Short,
 withDismissAction = true,
 )
 when (result) {
 SnackbarResult.ActionPerformed -> {
 Log.d("MainScreen", "Undo delete tapped ${link.name}")
 viewModel.undoDelete()
 }

 SnackbarResult.Dismissed -> {
 Log.d("MainScreen", "Snackbar dismissed")
 }
 }
 }
 },
 links = links,
 )
 }

 AnimatedVisibility(
 visible = isTopAppBarOffScreen,
 enter = fadeIn(),
 exit = fadeOut(),
 modifier = Modifier
 .align(Alignment.BottomEnd) // Position at bottom-right
 .padding(innerPadding) // Respect Scaffold padding
 .padding(16.dp), // Additional padding for the FAB
 ) {
 FloatingActionButton(
 onClick = {
 coroutineScope.launch {
 listState.animateScrollToItem(0) // Scroll LazyColumn to top
 // Optionally, also expand the TopAppBar fully
 scrollBehavior.state.heightOffset = 0f
 }
 },
 containerColor = MaterialTheme.colorScheme.secondary,
 contentColor = MaterialTheme.colorScheme.onSecondary,
 ) {
 Icon(Icons.Filled.ArrowUpward, "Scroll to top")
 }
 }
 }
 },
 )
}

@OptIn(ExperimentalMaterial3ExpressiveApi::class)
@Composable
private fun CollapsingHeader(viewModel: MainViewModel, isTopAppBarOffScreen: Boolean) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 ) {
 val allLinks by viewModel.allLinks.collectAsState()
 val options = listOf(LinkFilter.ALL, LinkFilter.VISITED, LinkFilter.UNVISITED)
 val currentFilter by viewModel.currentFilter.collectAsState()

 Row(
 horizontalArrangement = Arrangement.spacedBy(ToggleButtonDefaults.IconSpacing),
 modifier = Modifier.animateContentSize(),
 ) {
 if (allLinks.isNotEmpty()) {
 options.forEachIndexed { index, label ->
 ToggleButton(
 checked = currentFilter == label,
 onCheckedChange = {
 when (label) {
 LinkFilter.ALL -> viewModel.setFilter(LinkFilter.ALL)
 LinkFilter.VISITED -> viewModel.setFilter(LinkFilter.VISITED)
 LinkFilter.UNVISITED -> viewModel.setFilter(LinkFilter.UNVISITED)
 else -> {}
 }
 },
 modifier = Modifier.weight(1f),

 shapes =
 when (index) {
 0 -> ButtonGroupDefaults.connectedLeadingButtonShapes()
 options.lastIndex -> ButtonGroupDefaults.connectedTrailingButtonShapes()
 else -> ButtonGroupDefaults.connectedMiddleButtonShapes()
 },
 ) {
 if (currentFilter == label) {
 Icon(
 Icons.Rounded.Done,
 contentDescription = "Localized description",
 )
 }

 val text = when (label) {
 LinkFilter.ALL -> "All"
 LinkFilter.VISITED -> "Visited"
 LinkFilter.UNVISITED -> "Unvisited"
 else -> ""
 }
 Text(text, maxLines = 1)
 }
 }
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // Category filter chips
 val allCategories by viewModel.allCategories.collectAsState()
 val selectedCategoryIds by viewModel.selectedCategoryIds.collectAsState()

 if (allCategories.isNotEmpty()) {
 AnimatedVisibility(
 visible = !isTopAppBarOffScreen,
 enter = expandVertically(
 animationSpec = tween(500),
 ) + fadeIn(),
 exit = shrinkVertically(
 animationSpec = tween(500),
 ) + fadeOut(),
 ) {
 Text(
 text = "Filter by category".uppercase(),
 style = MaterialTheme.typography.titleSmallEmphasized,
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp, vertical = 4.dp),
 )
 }

 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 8.dp),
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 ) {
 // "All" chip
 item {
 androidx.compose.material3.FilterChip(
 selected = selectedCategoryIds.isEmpty(),
 onClick = { viewModel.selectCategoryFilter(null) },
 label = { Text("All") },
 modifier = Modifier.padding(vertical = 4.dp),
 )
 }

 // Category chips
 items(allCategories) { category ->
 androidx.compose.material3.FilterChip(
 selected = selectedCategoryIds.contains(category.id),
 leadingIcon = if (selectedCategoryIds.contains(category.id)) {
 {
 Icon(
 imageVector = Icons.Rounded.Check,
 contentDescription = "Done icon",
 modifier = Modifier.size(FilterChipDefaults.IconSize),
 )
 }
 } else {
 null
 },
 onClick = { viewModel.selectCategoryFilter(category.id) },
 label = { Text(category.name) },
 modifier = Modifier.padding(vertical = 4.dp),
 )
 }
 }

 Spacer(modifier = Modifier.height(8.dp))
 }
 }
}

@Composable
private fun LinksContent(
 viewModel: MainViewModel,
 listState: LazyListState,
 openBottomSheet: () -> Unit,
 onDelete: (Link) -> Unit,
 links: List<Link> = emptyList(),
) {
 val deletingLinkIds by viewModel.deletingLinkIds.collectAsState()
 val currentFilter by viewModel.currentFilter.collectAsState()

 LazyColumn(
 state = listState,
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 verticalArrangement = Arrangement.spacedBy(8.dp),
 ) {
 if (links.isEmpty()) {
 item {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 ) {
 // Customize empty state text based on current filter
 val emptyStateText = when (currentFilter) {
 LinkFilter.VISITED -> "No viewed links"
 LinkFilter.UNVISITED -> "No unviewed links"
 LinkFilter.ALL -> "No links added yet"
 LinkFilter.CATEGORY -> "No links in this category"
 }

 Text(
 text = emptyStateText,
 modifier = Modifier.fillMaxWidth(),
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onSurfaceVariant,
 )

 // Only show the full empty state UI for the ALL filter
 if (currentFilter == LinkFilter.ALL) {
 Spacer(modifier = Modifier.height(24.dp))
 Icon(
 modifier = Modifier
 .size(200.dp)
 .padding(16.dp),
 imageVector = Icons.Rounded.AddLink,
 contentDescription = "No links",
 tint = MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.5f),
 )
 Spacer(modifier = Modifier.height(16.dp))
 Button(
 onClick = openBottomSheet,
 colors = ButtonDefaults.buttonColors(
 containerColor = MaterialTheme.colorScheme.primary,
 ),
 modifier = Modifier.padding(horizontal = 16.dp),
 ) {
 Icon(
 imageVector = Icons.Rounded.Add,
 contentDescription = "Add Link",
 tint = MaterialTheme.colorScheme.onPrimary,
 modifier = Modifier.size(20.dp),
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(
 text = "Add Link",
 color = MaterialTheme.colorScheme.onPrimary,
 )
 }
 }
 }
 }
 } else {
 items(
 items = links,
 key = { link -> link.id },
 ) { link ->
 AnimatedVisibility(
 visible = !deletingLinkIds.contains(link.id),
 exit = slideOutHorizontally(
 targetOffsetX = { -it },
 animationSpec = tween(300),
 ),
 ) {
 LinkItem(link = link, viewModel = viewModel, onDelete = onDelete)
 }
 }
 }
 }
}

@Composable
fun LinkItem(link: Link, viewModel: MainViewModel, onDelete: (Link) -> Unit) {
 val context = LocalContext.current

 // State to track if the item is in edit mode
 var isEditing by rememberSaveable { mutableStateOf(false) }
 var editName by rememberSaveable { mutableStateOf(link.name ?: "") }
 var editUrl by rememberSaveable { mutableStateOf(link.uri.toString()) }

 // Observe edit state
 val editLinkUiState by viewModel.editLinkUiState.collectAsState()

 // Effect to handle edit state changes
 LaunchedEffect(editLinkUiState) {
 when (editLinkUiState) {
 is EditLinkUiState.Success -> {
 isEditing = false
 viewModel.resetEditState()
 }

 else -> {
 /* No action needed */
 }
 }
 }

 val dismissState = rememberSwipeToDismissBoxState(
 initialValue = SwipeToDismissBoxValue.Settled,
 positionalThreshold = { distance -> distance * 0.25f },
 )

 SwipeToDismissBox(
 state = dismissState,
 enableDismissFromStartToEnd = false,
 enableDismissFromEndToStart = true,
 backgroundContent = {
 DismissBackground()
 },
 onDismiss = {
 when (it) {
 SwipeToDismissBoxValue.EndToStart -> onDelete(link)
 else -> {}
 }
 },
 ) {
 ElevatedCard(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp)
 .clickable(enabled = !isEditing) {
 // Mark the link as visited
 viewModel.markLinkAsVisited(link)
 // Open the link in browser
 val intent = Intent(Intent.ACTION_VIEW, link.uri.toString().lowercase().toUri())
 context.startActivity(intent)
 },
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surfaceContainerHigh,
 ),
 ) {
 if (isEditing) {
 // Edit mode UI
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 ) {
 // Error message
 var localErrorMessage by remember { mutableStateOf<String?>(null) }

 if (editLinkUiState is EditLinkUiState.Error) {
 Text(
 text = (editLinkUiState as EditLinkUiState.Error).message,
 color = MaterialTheme.colorScheme.error,
 modifier = Modifier.padding(bottom = 8.dp),
 fontSize = 12.sp,
 )
 } else if (localErrorMessage != null) {
 Text(
 text = localErrorMessage!!,
 color = MaterialTheme.colorScheme.error,
 modifier = Modifier.padding(bottom = 8.dp),
 fontSize = 12.sp,
 )
 }

 // Name field
 OutlinedTextField(
 value = editName,
 onValueChange = { editName = it },
 label = { Text("Name") },
 modifier = Modifier.fillMaxWidth(),
 singleLine = true,
 colors = TextFieldDefaults.colors(),
 keyboardOptions =
 KeyboardOptions(
 imeAction = ImeAction.Done,
 capitalization = KeyboardCapitalization.Sentences,
 ),
 )

 Spacer(modifier = Modifier.height(8.dp))

 // URL field
 OutlinedTextField(
 value = editUrl,
 onValueChange = { editUrl = it },
 label = { Text("URL") },
 modifier = Modifier.fillMaxWidth(),
 singleLine = true,
 colors = TextFieldDefaults.colors(),
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Categories field
 val categories = remember(link.id) { mutableStateOf<List<Category>>(emptyList()) }
 var showEditCategoryDialog by remember { mutableStateOf(false) }

 LaunchedEffect(link.categoryIds) {
 categories.value = viewModel.getCategoriesForLink(link)
 // Initialize selected categories with the link's current categories
 viewModel.clearSelectedCategories()
 categories.value.forEach { category ->
 viewModel.selectCategory(category)
 }
 }

 // Show category creation dialog
 CategoryDialog(
 viewModel = viewModel,
 showDialog = showEditCategoryDialog,
 onDismiss = { showEditCategoryDialog = false },
 )

 // Categories section
 Text(
 text = "Categories".uppercase(),
 style = MaterialTheme.typography.labelMedium,
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp, bottom = 4.dp),
 )

 // Show all categories as filter chips
 val allCategories by viewModel.allCategories.collectAsState()
 val selectedCategories by viewModel.selectedCategories.collectAsState()

 if (allCategories.isNotEmpty()) {
 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 8.dp),
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 ) {
 items(allCategories) { category ->
 val isSelected = selectedCategories.contains(category)
 androidx.compose.material3.FilterChip(
 selected = isSelected,
 leadingIcon = if (isSelected) {
 {
 Icon(
 imageVector = Icons.Rounded.Done,
 contentDescription = "Done icon",
 modifier = Modifier.size(FilterChipDefaults.IconSize),
 )
 }
 } else {
 null
 },
 onClick = {
 if (isSelected) {
 viewModel.unselectCategory(category)
 } else {
 viewModel.selectCategory(category)
 }
 },
 label = { Text(category.name) },
 )
 }
 }
 }

 // Button to create a new category
 Button(
 onClick = { showEditCategoryDialog = true },
 modifier = Modifier.fillMaxWidth(),
 colors = ButtonDefaults.buttonColors(
 containerColor = MaterialTheme.colorScheme.secondary,
 ),
 ) {
 Text("Create New Category")
 }

 Spacer(modifier = Modifier.height(16.dp))

 // Action buttons
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.End,
 ) {
 TextButton(
 onClick = {
 isEditing = false
 editName = link.name ?: ""
 editUrl = link.uri.toString()
 viewModel.resetEditState()
 },
 ) {
 Text("Cancel")
 }

 Spacer(modifier = Modifier.width(8.dp))

 Button(
 onClick = {
 if (selectedCategories.isEmpty()) {
 // Show error if no categories selected
 localErrorMessage = "Please select at least one category"
 } else {
 localErrorMessage = null
 val categoryNames = selectedCategories.map { it.name }
 viewModel.editLink(link, editName, editUrl, categoryNames)
 }
 },
 enabled = editLinkUiState !is EditLinkUiState.Loading,
 ) {
 if (editLinkUiState is EditLinkUiState.Loading) {
 CircularProgressIndicator(
 modifier = Modifier.size(24.dp),
 color = MaterialTheme.colorScheme.onPrimary,
 )
 } else {
 Text("Save")
 }
 }
 }
 }
 } else {
 // View mode UI
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically,
 ) {
 Column(
 modifier = Modifier
 .weight(1f)
 .padding(end = 16.dp),
 ) {
 Text(
 text = link.name ?: "Untitled",
 fontSize = 18.sp,
 fontWeight = if (link.visited) FontWeight.Normal else FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = link.uri.toString(),
 fontSize = 12.sp,
 color = MaterialTheme.colorScheme.onSurfaceVariant,
 maxLines = 1,
 overflow = TextOverflow.Ellipsis,
 )
 Spacer(modifier = Modifier.height(4.dp))
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(4.dp),
 ) {
 // Display unviewed/viewed label
 Text(
 text = if (link.visited) "Viewed" else "Unviewed",
 fontSize = 10.sp,
 color = if (link.visited) {
 MaterialTheme.colorScheme.primary
 } else {
 MaterialTheme.colorScheme.tertiary
 },
 fontWeight = FontWeight.Normal,
 )

 // Display categories if any
 if (link.categoryIds.isNotEmpty()) {
 val categories = remember(link.categoryIds) {
 mutableStateOf<List<Category>>(emptyList())
 }

 // Load categories for this link
 LaunchedEffect(link.categoryIds) {
 categories.value = viewModel.getCategoriesForLink(link)
 }

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(4.dp),
 modifier = Modifier.padding(start = 8.dp),
 ) {
 items(categories.value) { category ->
 SuggestionChip(
 modifier = Modifier.height(24.dp),
 colors = SuggestionChipDefaults.suggestionChipColors(
 containerColor = MaterialTheme.colorScheme.secondaryContainer,
 labelColor = MaterialTheme.colorScheme.onSurface,
 ),
 border = BorderStroke(
 width = 1.dp,
 color = Color.Transparent,
 ),
 onClick = { },
 label = {
 Text(
 text = category.name,
 fontSize = 10.sp,
 )
 },
 )
 }
 }
 }
 }
 }
 Column(
 horizontalAlignment = Alignment.End,
 ) {
 Row(
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 verticalAlignment = Alignment.CenterVertically,
 ) {
 IconButton(
 onClick = {
 val sendIntent = Intent().apply {
 action = Intent.ACTION_SEND
 putExtra(Intent.EXTRA_TEXT, link.uri.toString())
 type = "text/plain"
 }
 context.startActivity(Intent.createChooser(sendIntent, null))
 },

 colors = IconButtonDefaults.iconButtonColors(
 contentColor = MaterialTheme.colorScheme.onPrimary,
 containerColor = MaterialTheme.colorScheme.primary,
 ),

 ) {
 Icon(
 imageVector = Icons.Rounded.Share,
 contentDescription = "Share link",
 modifier = Modifier
 .size(24.dp),
 )
 }

 IconButton(
 onClick = { isEditing = true },
 colors = IconButtonDefaults.iconButtonColors(
 contentColor = MaterialTheme.colorScheme.onPrimary,
 containerColor = MaterialTheme.colorScheme.primary,
 ),
 ) {
 Icon(
 imageVector = Icons.Rounded.EditNote,
 contentDescription = "Edit link",
 modifier = Modifier
 .size(24.dp),
 )
 }
 }
 }
 }
 }
 }
 }
}

@Composable
fun CategoryDialog(
 viewModel: MainViewModel,
 showDialog: Boolean,
 onDismiss: () -> Unit,
) {
 val categoryUiState by viewModel.categoryUiState.collectAsState()
 var categoryName by rememberSaveable { mutableStateOf("") }
 var errorMessage by remember { mutableStateOf<String?>(null) }

 LaunchedEffect(categoryUiState) {
 when (categoryUiState) {
 is CategoryUiState.Error -> {
 errorMessage = (categoryUiState as CategoryUiState.Error).message
 }

 is CategoryUiState.Success -> {
 errorMessage = null
 categoryName = ""
 onDismiss()
 viewModel.resetCategoryState()
 }

 else -> {
 errorMessage = null
 }
 }
 }

 if (showDialog) {
 androidx.compose.material3.AlertDialog(
 onDismissRequest = {
 onDismiss()
 viewModel.resetCategoryState()
 },
 title = { Text("Create Category") },
 text = {
 Column {
 if (errorMessage != null) {
 Text(
 text = errorMessage!!,
 color = MaterialTheme.colorScheme.error,
 modifier = Modifier.padding(bottom = 8.dp),
 )
 }

 OutlinedTextField(
 value = categoryName,
 onValueChange = { categoryName = it },
 label = { Text("Category Name") },
 singleLine = true,
 modifier = Modifier.fillMaxWidth(),
 isError = errorMessage != null,
 keyboardOptions = KeyboardOptions(
 imeAction = ImeAction.Done,
 capitalization = KeyboardCapitalization.Sentences,
 ),
 keyboardActions = KeyboardActions(
 onDone = {
 viewModel.addCategory(categoryName)
 },
 ),
 )
 }
 },
 confirmButton = {
 Button(
 onClick = {
 viewModel.addCategory(categoryName)
 },
 enabled = categoryUiState !is CategoryUiState.Loading,
 ) {
 if (categoryUiState is CategoryUiState.Loading) {
 CircularProgressIndicator(
 modifier = Modifier.size(24.dp),
 color = MaterialTheme.colorScheme.onPrimary,
 )
 } else {
 Text("Create")
 }
 }
 },
 dismissButton = {
 TextButton(
 onClick = {
 onDismiss()
 viewModel.resetCategoryState()
 },
 ) {
 Text("Cancel")
 }
 },
 )
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ModalBottomSheetAddUrl(
 viewModel: MainViewModel = hiltViewModel(),
 openBottomSheet: Boolean = false,
 onOpenBottomSheetChange: (Boolean) -> Unit = {},
) {
 var url by rememberSaveable { mutableStateOf("") }
 var name by rememberSaveable { mutableStateOf("") }
 var showCategoryDialog by remember { mutableStateOf(false) }
 var errorMessage by remember { mutableStateOf<String?>(null) }

 val scope = rememberCoroutineScope()
 val bottomSheetState = rememberModalBottomSheetState(skipPartiallyExpanded = true)
 val nameFocusRequester = remember { FocusRequester() }

 val uiState by viewModel.uiState.collectAsState()
 val allCategories by viewModel.allCategories.collectAsState()
 val selectedCategories by viewModel.selectedCategories.collectAsState()
 val sharedUrl by viewModel.sharedUrl.collectAsState()

 // Show category creation dialog
 CategoryDialog(
 viewModel = viewModel,
 showDialog = showCategoryDialog,
 onDismiss = { showCategoryDialog = false },
 )

 LaunchedEffect(uiState) {
 when (uiState) {
 is AddLinkUiState.Error -> {
 errorMessage = (uiState as AddLinkUiState.Error).message
 }

 is AddLinkUiState.Success -> {
 errorMessage = null
 url = ""
 name = ""
 viewModel.clearSelectedCategories()
 viewModel.clearSharedUrl()
 scope.launch {
 bottomSheetState.hide()
 onOpenBottomSheetChange(false)
 }
 viewModel.resetState()
 }

 else -> {
 errorMessage = null
 }
 }
 }

 // Handle shared URL pre-filling
 LaunchedEffect(sharedUrl) {
 sharedUrl?.let { sharedUrlValue ->
 url = sharedUrlValue
 }
 }

 // Sheet content
 if (openBottomSheet) {
 ModalBottomSheet(
 onDismissRequest = {
 onOpenBottomSheetChange(false)
 errorMessage = null
 viewModel.resetState()
 viewModel.clearSelectedCategories()
 viewModel.clearSharedUrl()
 },
 sheetState = bottomSheetState,
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 ) {
 // Cancel button
 Row(
 Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.Center,
 ) {
 TextButton(
 onClick = {
 scope.launch {
 bottomSheetState.hide()
 onOpenBottomSheetChange(false)
 errorMessage = null
 viewModel.resetState()
 viewModel.clearSelectedCategories()
 viewModel.clearSharedUrl()
 }
 },
 ) {
 Text(
 fontSize = 22.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary,
 text = "Cancel",
 )
 }
 }

 // Error message
 if (errorMessage != null) {
 Text(
 text = errorMessage!!,
 color = MaterialTheme.colorScheme.error,
 modifier = Modifier.padding(vertical = 8.dp),
 )
 }

 // URL Input Field
 OutlinedTextField(
 value = url,
 onValueChange = { url = it },
 label = { Text("URL") },
 modifier = Modifier.fillMaxWidth(),
 singleLine = true,
 colors = TextFieldDefaults.colors(),
 keyboardOptions = KeyboardOptions(imeAction = ImeAction.Next),
 keyboardActions = KeyboardActions(
 onNext = {
 nameFocusRequester.requestFocus()
 },
 ),
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Name Input Field
 OutlinedTextField(
 value = name,
 onValueChange = { name = it },
 label = { Text("Name") },
 modifier = Modifier
 .fillMaxWidth()
 .focusRequester(nameFocusRequester),
 singleLine = true,
 colors = TextFieldDefaults.colors(),
 keyboardOptions = KeyboardOptions(imeAction = ImeAction.Done),
 keyboardActions = KeyboardActions(
 onDone = {
 // No action needed here
 },
 ),
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Categories Section

 Spacer(modifier = Modifier.height(8.dp))

 // Category Chip Group
 if (allCategories.isNotEmpty()) {
 Text(
 text = "Categories".uppercase(),
 style = MaterialTheme.typography.labelMedium,
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp, bottom = 4.dp),
 )

 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 8.dp),
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 ) {
 items(allCategories) { category ->
 val isSelected = selectedCategories.contains(category)
 androidx.compose.material3.FilterChip(
 selected = isSelected,
 leadingIcon = if (isSelected) {
 {
 Icon(
 imageVector = Icons.Rounded.Done,
 contentDescription = "Done icon",
 modifier = Modifier.size(FilterChipDefaults.IconSize),
 )
 }
 } else {
 null
 },
 onClick = {
 if (isSelected) {
 viewModel.unselectCategory(category)
 } else {
 viewModel.selectCategory(category)
 }
 },
 label = { Text(category.name) },
 )
 }
 }
 }

 // Button to create a new category
 Button(
 onClick = { showCategoryDialog = true },
 modifier = Modifier.fillMaxWidth(),
 colors = ButtonDefaults.buttonColors(
 containerColor = MaterialTheme.colorScheme.secondary,
 ),
 ) {
 Text("Create New Category")
 }

 Spacer(modifier = Modifier.height(16.dp))

 // Submit button
 Button(
 onClick = {
 if (selectedCategories.isEmpty()) {
 errorMessage = "Please select at least one category"
 } else {
 val categoryNames = selectedCategories.map { it.name }
 viewModel.addLink(name, url, categoryNames)
 }
 },
 modifier = Modifier.fillMaxWidth(),
 enabled = uiState !is AddLinkUiState.Loading,
 ) {
 if (uiState is AddLinkUiState.Loading) {
 CircularProgressIndicator(
 modifier = Modifier.size(24.dp),
 color = MaterialTheme.colorScheme.onPrimary,
 )
 } else {
 Text(stringResource(R.string.submit))
 }
 }
 }
 }
 }
}

